import React from "react";

// Get the video
var video = document.getElementById("myVideo");

// Get the button
var btn = document.getElementById("myBtn");

// Pause and play the video, and change the button text
function myFunction() {
  if (video.paused) {
    video.play();
    btn.innerHTML = "Pause";
  } else {
    video.pause();
    btn.innerHTML = "Play";
  }
}

function Header() {
    return (
    <div>
        <header>
            <h2><span>WELCOME</span><span> to the</span></h2>
            <h1>Laguna Cafe</h1>

            <video autoplay muted loop id="myVideo">
                <source src="../images/IMG_0277.mov" type="video/mov"></source>
            </video>

<div class="content">
  <p>Press Button to Play video.</p>

  <button id="myBtn" onclick="myFunction()">Pause</button>
</div>
        </header>
    </div>
    );
}



export default Header
