import React from "react";
import "./contactstyles.css";
import Navbar from "./Navbar";
import Footer from "./Footer";

const Contact = () => {
    return (
        <div>
            <Navbar />     
            <h1 class="heading">Contact Us</h1>
            <h2 class="heading">We welcome your feedback!</h2>
            <div>
                <div class="contact-form-wrapper d-flex justify-content-center">
                    <form action="#" class="contact-form">
                        <h5 class="title">Contact us</h5>
                        <p class="description">Feel free to contact us and we welcome all feedback!</p>
                        <div>
                            <input type="text" class="form-control rounded border-white mb-3 form-input" id="name" placeholder="Name" required/>
                        </div>
                        <div>
                            <input type="email" class="form-control rounded border-white mb-3 form-input" placeholder="Email" required/>
                        </div>
                        <div>
                            <textarea id="message" class="form-control rounded border-white mb-3 form-text-area" rows="5" cols="30" placeholder="Message" required></textarea>
                        </div>
                        <div class="submit-button-wrapper">
                            <input type="submit" value="Send"/>
                        </div>
                    </form>
                </div>
            </div>
            {/* <div class="map">
                <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d97810.06232955445!2d-74.23465487902206!3d39.99583782130813!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89c19ce4a38e7105%3A0x3f5b6f5b358b3cb0!2sToms%20River%2C%20NJ!5e0!3m2!1sen!2sus!4v1667577813865!5m2!1sen!2sus" width="600" height="450" style="border:0;" allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
            </div> */}
            <Footer />
        </div>
    );
};

export default Contact;
